from urllib.request import urlopen
from bs4 import BeautifulSoup
import pandas as pd
from unidecode import unidecode


def get_player_url(player_name, player_team):
    first,last = player_name.split()
    i = 0
    found = False
    while not found:
        url = 'https://www.pro-football-reference.com/players/{}/{}{}0{}.htm'.format(last[0], last[:4], first[:2], i)
        
        html = urlopen(url)
        soup = BeautifulSoup(html, features = 'lxml')
        team = soup.find(id='meta')
        team_url = team.find_all('a')[0]
        
        if player_team in str(team_url):
            found=True
        else:
            i+=1
            if i==10:
                found=True

    return url

url = get_player_url('Stefon Diggs', 'buf')

player_html = urlopen(url)
soup = BeautifulSoup(player_html, features = 'html.parser')#'lxml')
advanced_stats = soup.find(id = 'div_detailed_receiving_and_rushing')

def beautify_and_save_to_file(input_string, file_name):
    # Beautify the input string (you can replace this with your own beautification logic)
    beautified_string = input_string.prettify()  # Just an example, you can beautify it as you wish
    
    # Write the beautified string to the specified file
    with open(file_name, 'w', encoding = 'utf-8') as file:
        file.write(beautified_string)

# Example usage
input_text = soup
output_file = "beautified_output.txt"
beautify_and_save_to_file(input_text, output_file)
print(f"Beautified string saved to '{output_file}'.")


